/* eslint-disable jsx-a11y/anchor-is-valid */
import { useState } from 'react';

function Menu(){
    const [isWallpMenuVisible, setWallpMenuVisibility] = useState(false);
    const showWallpMenu = () => {
        setWallpMenuVisibility(!isWallpMenuVisible);
    }

    const [isPaintMenuVisible, setPaintMenuVisibility] = useState(false);
    const showPaintMenu = () => {
        setPaintMenuVisibility(!isPaintMenuVisible);
    }

    const [isBrandsMenuVisible, setBrandsMenuVisibility] = useState(false);
    const showBrandsMenu = () => {
        setBrandsMenuVisibility(!isBrandsMenuVisible);
    }

    const [isWallArtMenuVisible, setWallArtMenuVisibility] = useState(false);
    const showWallArtMenu = () => {
        setWallArtMenuVisibility(!isWallArtMenuVisible);
    }

    const showNav = () => {
        setNavVisibility(!isNavVisible);
    };

    return (
        <div className="navigation-menu-all">
            <nav>
                <ul className="menu-categories">
                    <li>
                        <a onMouseOver={showWallpMenu} className="menu-category uppercase">Wallpaper <i className="arrow-down"></i></a>
                    </li>
                    <li>
                        <a onMouseOver={showPaintMenu} className="menu-category uppercase">Paint <i className="arrow-down"></i> </a>
                    </li>
                    <li>
                        <a onMouseOver={showBrandsMenu} className="menu-category uppercase">Brands <i className="arrow-down"></i> </a>
                    </li>
                    <li>
                        <a className="menu-category uppercase">Wall Murals</a>
                    </li>
                    <li>
                        <a onMouseOver={showWallArtMenu} className="menu-category uppercase">Wall Art <i className="arrow-down"></i> </a>
                    </li>
                    <li>
                        <a className="menu-category uppercase">Sale</a>
                    </li>
                </ul>
            </nav>
            {isWallpMenuVisible && (
                <div className="nav-show">
                    <ul>
                        <li><a className="menu-option">Animal </a></li>
                        <li><a className="menu-option">Bird </a></li>
                        <li><a className="menu-option">Brick </a></li>
                        <li><a className="menu-option">Damask</a></li>
                        <li><a className="menu-option">Floral </a></li>
                        <li><a className="menu-option">Geometric</a></li>
                    </ul>
                </div>
            )}  
        </div>
    );
}

export default Menu;
